    <script>
        starry({
            target: document.getElementById("demo"),
            max: 5,
            now: 0,
        });

        function starry(instance) {
            for (let i = 1; i <= 5; i++) {
                let b = document.createElementNS('http://www.w3.org/2000/svg', "svg");
                var newElement = document.createElementNS("http://www.w3.org/2000/svg",
                    'path'); //Create a path in SVG's namespace
                newElement.setAttribute("d",
                    "M11.1157 2.81994C11.4824 2.07605 12.5431 2.07605 12.9097 2.81994L15.1881 7.44276C15.3338 7.73833 15.6157 7.94316 15.9418 7.99037L21.0001 8.72264C21.8194 8.84125 22.1481 9.84709 21.5568 10.4265L17.8802 14.0297C17.646 14.2592 17.5392 14.5889 17.5944 14.9121L18.4618 19.9969C18.6013 20.8149 17.7412 21.4372 17.0078 21.0487L12.4809 18.6502C12.1881 18.4951 11.8374 18.4951 11.5446 18.6502L7.0176 21.0487C6.28427 21.4372 5.42412 20.8149 5.56367 19.9969L6.43081 14.9135C6.48608 14.5896 6.37861 14.2591 6.14334 14.0296L2.45153 10.4281C1.85803 9.84917 2.18639 8.84098 3.00701 8.72258L8.0833 7.99016C8.4096 7.94308 8.69173 7.7382 8.83748 7.44249L11.1157 2.81994Z"
                ); //Set path's data
                newElement.style.stroke = "#121219"; //Set stroke colour
                newElement.style.strokeWidth = "1.5"; //Set stroke width
                b.classList.add('star');
                b.setAttributeNS(null, "width", 24);
                b.setAttributeNS(null, "height", 24);

                b.appendChild(newElement);
                instance.target.appendChild(b);

                // (B3) ON MOUSE OVER
                b.onclick = () => {
                    let all = instance.target.getElementsByClassName("star");
                    for (let j = 0; j < all.length; j++) {
                        if (j < i) {
                            all[j].classList.add("fillcolor");
                            all[j].querySelector('path').style.stroke = "none";
                        } else {
                            all[j].classList.remove("fillcolor");
                            all[j].querySelector('path').style.stroke = "#121219";
                        }
                    }
                };
            };

        }
    </script>
    <script src="https://cdn.jsdelivr.net/npm/jquery@3.6.0/dist/jquery.min.js"></script>
    <script type="text/javascript">
        $(document).ready(function() {

            $('#change').click(function(event) {
                var qtystar = document.getElementById("demo").querySelectorAll(".fillcolor").length;
                var madt = document.getElementById('madt').value;
                var noidung = document.getElementById('noidung').value;
                $.ajax({
                    url: "/api/star",
                    type: 'POST',
                    data: {
                        madt: madt,
                        id: qtystar,
                        noidung:noidung,
                        _token: "{{ csrf_token() }}"
                    },
                    cache: false,
                    success: function(response) {
                        window.location.href = '/danhgiathanhcong'

                    }
                })
            });
        });
    </script>
